	public function run($nn, $tt, $nnnn, $gioSinh, $gioiTinh, $duongLich, $timeZone)
	{
		$helper = new Helpers();
		$this->setSao();
		if ($duongLich) {
			$ngaythangnam = $helper->ngayThangNam($nn, $tt, $nnnn, $timeZone);
			if (is_array($ngaythangnam)) {
				list($nn, $tt, $nnnn, $thangNhuan) = $ngaythangnam;
			}
		}
		list($canThang, $canNam, $chiNam) = $helper->ngayThangNamCanChi($nn, $tt, $nnnn, false, $timeZone);
		$diaBan = new diaBan($tt, $gioSinh);

		$thienCan = Constants::$thienCan;
		$diaChi = Constants::$diaChi;
		$amDuongNamSinh = $thienCan[$canNam]["amDuong"];
		$amDuongChiNamSinh = $diaChi[$chiNam]["amDuong"];

		# Bản Mệnh chính là Ngũ hành nạp âm của năm sinh
		# banMenh = nguHanhNapAm(canNam, chiNam)
		$hanhCuc = $helper->timCuc($diaBan->cungMenh, $canNam);
		$cuc = $helper->nguHanh($hanhCuc);
		$cucSo = $cuc['cuc'];

		# Nhập đại hạn khi đã biết được số cục
		# Theo sách Số tử vi dưới góc nhìn khoa học
		# Dương Nam - Âm Nữ theo chiều thuận
		# Âm Nam - Dương Nữ theo chiều nghịch
		$diaBan->nhapDaiHan($cucSo, $gioiTinh * $amDuongChiNamSinh);

		# Nhập tiểu hạn
		$khoiHan = $helper->dichCung(11, [-3 * ($chiNam - 1)]);
		$diaBan->nhapTieuHan($khoiHan, $gioiTinh, $chiNam);

		# Bắt đầu an Tử vi tinh hệ
		$viTriTuVi = $helper->timTuVi($cucSo, $nn);
		$diaBan->nhapSao($viTriTuVi, [$this->saoTuVi]);

		$viTriLiemTrinh = $helper->dichCung($viTriTuVi, [4]);
		$diaBan->nhapSao($viTriLiemTrinh, [$this->saoLiemTrinh]);

		$viTriThienDong = $helper->dichCung($viTriTuVi, [7]);
		$diaBan->nhapSao($viTriThienDong, [$this->saoThienDong]);

		$viTriVuKhuc = $helper->dichCung($viTriTuVi, [8]);
		$diaBan->nhapSao($viTriVuKhuc, [$this->saoVuKhuc]);

		$vitriThaiDuong = $helper->dichCung($viTriTuVi, [9]);
		$diaBan->nhapSao($vitriThaiDuong, [$this->saoThaiDuong]);

		$viTriThienCo = $helper->dichCung($viTriTuVi, [11]);
		$diaBan->nhapSao($viTriThienCo, [$this->saoThienCo]);

		# Thiên phủ tinh hệ
		# viTriTuVi = 4
		$viTriThienPhu = $helper->dichCung(3, [3 - (int)$viTriTuVi]);
		$diaBan->nhapSao($viTriThienPhu, [$this->saoThienPhu]);

		$viTriThaiAm = $helper->dichCung($viTriThienPhu, [1]);
		$diaBan->nhapSao($viTriThaiAm, [$this->saoThaiAm]);

		$viTriThamLang = $helper->dichCung($viTriThienPhu, [2]);
		$diaBan->nhapSao($viTriThamLang, [$this->saoThamLang]);

		$viTriCuMon = $helper->dichCung($viTriThienPhu, [3]);
		$diaBan->nhapSao($viTriCuMon, [$this->saoCuMon]);

		$viTriThienTuong = $helper->dichCung($viTriThienPhu, [4]);
		$diaBan->nhapSao($viTriThienTuong, [$this->saoThienTuong]);

		$viTriThienLuong = $helper->dichCung($viTriThienPhu, [5]);
		$diaBan->nhapSao($viTriThienLuong, [$this->saoThienLuong]);

		$viTriThatSat = $helper->dichCung($viTriThienPhu, [6]);
		$diaBan->nhapSao($viTriThatSat, [$this->saoThatSat]);

		$viTriPhaQuan = $helper->dichCung($viTriThienPhu, [10]);
		$diaBan->nhapSao($viTriPhaQuan, [$this->saoPhaQuan]);

		# Vòng Lộc tồn
		# Vị trí sao Lộc tồn ở Can của năm sinh trên địa bàn
		#  sao Bác sỹ ở cùng cung với Lộc tồn
		$viTriLocTon = $thienCan[$canNam]['vitriDiaBan'];
		$diaBan->nhapSao($viTriLocTon, [$this->saoLocTon, $this->saoBacSy]);

		$amDuongNamNu = $gioiTinh * $amDuongNamSinh;
		$viTriLucSi = $helper->dichCung($viTriLocTon, [1 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriLucSi, [$this->saoLucSi]);

		$viTriThanhLong = $helper->dichCung($viTriLocTon, [2 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriThanhLong, [$this->saoThanhLong]);

		$viTriTieuHao = $helper->dichCung($viTriLocTon, [3 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriTieuHao, [$this->saoTieuHao]);

		$viTriTuongQuan = $helper->dichCung($viTriLocTon, [4 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriTuongQuan, [$this->saoTuongQuan]);

		$viTriTauThu = $helper->dichCung($viTriLocTon, [5 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriTauThu, [$this->saoTauThu]);

		$viTriPhiLiem = $helper->dichCung($viTriLocTon, [6 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriPhiLiem, [$this->saoPhiLiem]);

		$viTriHyThan = $helper->dichCung($viTriLocTon, [7 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriHyThan, [$this->saoHyThan]);

		$viTriBenhPhu = $helper->dichCung($viTriLocTon, [8 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriBenhPhu, [$this->saoBenhPhu]);

		$viTriDaiHao = $helper->dichCung($viTriLocTon, [9 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriDaiHao, [$this->saoDaiHao]);

		$viTriPhucBinh = $helper->dichCung($viTriLocTon, [10 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriPhucBinh, [$this->saoPhucBinh]);

		$viTriQuanPhu2 = $helper->dichCung($viTriLocTon, [11 * $amDuongNamNu]);
		$diaBan->nhapSao($viTriQuanPhu2, [$this->saoQuanPhu2]);

		# Vòng Địa chi - Thái tuế
		$viTriThaiTue = $chiNam;
		$diaBan->nhapSao($viTriThaiTue, [$this->saoThaiTue]);

		$viTriThieuDuong = $helper->dichCung($viTriThaiTue, [1]);
		$diaBan->nhapSao($viTriThieuDuong, [$this->saoThieuDuong, $this->saoThienKhong]);

		$viTriTangMon = $helper->dichCung($viTriThaiTue, [2]);
		$diaBan->nhapSao($viTriTangMon, [$this->saoTangMon]);

		$viTriThieuAm = $helper->dichCung($viTriThaiTue, [3]);
		$diaBan->nhapSao($viTriThieuAm, [$this->saoThieuAm]);

		$viTriQuanPhu3 = $helper->dichCung($viTriThaiTue, [4]);
		$diaBan->nhapSao($viTriQuanPhu3, [$this->saoQuanPhu3]);

		$viTriTuPhu = $helper->dichCung($viTriThaiTue, [5]);
		$diaBan->nhapSao($viTriTuPhu, [$this->saoTuPhu, $this->saoNguyetDuc]);

		$viTriTuePha = $helper->dichCung($viTriThaiTue, [6]);
		$diaBan->nhapSao($viTriTuePha, [$this->saoTuePha]);

		$viTriLongDuc = $helper->dichCung($viTriThaiTue, [7]);
		$diaBan->nhapSao($viTriLongDuc, [$this->saoLongDuc]);

		$viTriBachHo = $helper->dichCung($viTriThaiTue, [8]);
		$diaBan->nhapSao($viTriBachHo, [$this->saoBachHo]);

		$viTriPhucDuc = $helper->dichCung($viTriThaiTue, [9]);
		$diaBan->nhapSao($viTriPhucDuc, [$this->saoPhucDuc, $this->saoThienDuc]);

		$viTriDieuKhach = $helper->dichCung($viTriThaiTue, [10]);
		$diaBan->nhapSao($viTriDieuKhach, [$this->saoDieuKhach]);

		$viTriTrucPhu = $helper->dichCung($viTriThaiTue, [11]);
		$diaBan->nhapSao($viTriTrucPhu, [$this->saoTrucPhu]);

		#  Vòng ngũ hành cục Tràng sinh
		# !!! Đã sửa !!! *LƯU Ý Phần này đã sửa* Theo cụ Thiên Lương: Nam -> Thuận,
		# Nữ -> Nghịch (Không phù hợp)
		# **ISSUE 2**: Dương nam, Âm nữ theo chiều thuận, Âm nam Dương nữ theo
		# chiều nghịch

		$viTriTrangSinh = $helper->timTrangSinh($cucSo);
		$diaBan->nhapSao($viTriTrangSinh, [$this->saoTrangSinh]);

		$viTriMocDuc = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 1]);
		$diaBan->nhapSao($viTriMocDuc, [$this->saoMocDuc]);

		$viTriQuanDoi = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 2]);
		$diaBan->nhapSao($viTriQuanDoi, [$this->saoQuanDoi]);

		$viTriLamQuan = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 3]);
		$diaBan->nhapSao($viTriLamQuan, [$this->saoLamQuan]);

		$viTriDeVuong = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 4]);
		$diaBan->nhapSao($viTriDeVuong, [$this->saoDeVuong]);

		$viTriSuy = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 5]);
		$diaBan->nhapSao($viTriSuy, [$this->saoSuy]);

		$viTriBenh = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 6]);
		$diaBan->nhapSao($viTriBenh, [$this->saoBenh]);

		$viTriTu = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 7]);
		$diaBan->nhapSao($viTriTu, [$this->saoTu]);

		$viTriMo = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 8]);
		$diaBan->nhapSao($viTriMo, [$this->saoMo]);

		$viTriTuyet = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * 9]);
		$diaBan->nhapSao($viTriTuyet, [$this->saoTuyet]);

		$viTriThai = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * (-1)]);
		$diaBan->nhapSao($viTriThai, [$this->saoThai]);

		$viTriDuong = $helper->dichCung($viTriTrangSinh, [$amDuongNamNu * (-2)]);
		$diaBan->nhapSao($viTriDuong, [$this->saoDuong]);

		# An sao đôi
		#    Kình dương - Đà la
		$viTriDaLa = $helper->dichCung($viTriLocTon, [-1]);
		$diaBan->nhapSao($viTriDaLa, [$this->saoDaLa]);

		$viTriKinhDuong = $helper->dichCung($viTriLocTon, [1]);
		$diaBan->nhapSao($viTriKinhDuong, [$this->saoKinhDuong]);

		#  Không - Kiếp
		# Khởi giờ Tý ở cung Hợi, đếm thuận đến giờ sinh được cung Địa kiếp
		$viTriDiaKiep = $helper->dichCung(11, [$gioSinh]);
		$diaBan->nhapSao($viTriDiaKiep, [$this->saoDiaKiep]);

		$viTriDiaKhong = $helper->dichCung(12, [12 - $viTriDiaKiep]);
		$diaBan->nhapSao($viTriDiaKhong, [$this->saoDiaKhong]);

		list($viTriHoaTinh, $viTriLinhTinh) = $helper->timHoaLinh($chiNam, $gioSinh, $gioiTinh, $amDuongNamSinh);
		$diaBan->nhapSao($viTriHoaTinh, [$this->saoHoaTinh]);
		$diaBan->nhapSao($viTriLinhTinh, [$this->saoLinhTinh]);

		$viTriLongTri = $helper->dichCung(5, [$chiNam - 1]);
		$diaBan->nhapSao($viTriLongTri, [$this->saoLongTri]);

		$viTriPhuongCac = $helper->dichCung(2, [2 - $viTriLongTri]);
		$diaBan->nhapSao($viTriPhuongCac, [$this->saoPhuongCac, $this->saoGiaiThan]);

		$viTriTaPhu = $helper->dichCung(5, [$tt - 1]);
		$diaBan->nhapSao($viTriTaPhu, [$this->saoTaPhu]);

		$viTriHuuBat = $helper->dichCung(2, [2 - $viTriTaPhu]);
		$diaBan->nhapSao($viTriHuuBat, [$this->saoHuuBat]);

		$viTriVanKhuc = $helper->dichCung(5, [$gioSinh - 1]);
		$diaBan->nhapSao($viTriVanKhuc, [$this->saoVanKhuc]);

		$viTriVanXuong = $helper->dichCung(2, [2 - $viTriVanKhuc]);
		$diaBan->nhapSao($viTriVanXuong, [$this->saoVanXuong]);

		$viTriTamThai = $helper->dichCung(5, [$tt + $nn - 2]);
		$diaBan->nhapSao($viTriTamThai, [$this->saoTamThai]);

		$viTriBatToa = $helper->dichCung(2, [2 - $viTriTamThai]);
		$diaBan->nhapSao($viTriBatToa, [$this->saoBatToa]);

		# ! Vị trí sao Ân Quang - Thiên Quý
		# ! Lấy cung thìn làm mồng 1 đếm thuận đến ngày sinh,
		# ! lui lại một cung để lấy đó làm giờ tý đếm thuận đến giờ sinh là
		#  Ân Quang
		# ! Thiên Quý đối với Ân Quang qua trục Sửu Mùi
		# @ viTriAnQuang = dichCung(5, nn + gioSinh - 3)
		# @ viTriThienQuy = dichCung(2, 2 - viTriAnQuang)
		# Phía trên là cách an Quang-Quý theo cụ Vu Thiên
		# Sau khi tìm hiểu thì Quang-Quý sẽ được an theo Xương-Khúc như sau:
		# Ân Quang − Xem Văn Xương ở cung nào, kể cung ấy là mồng một
		# bắt đầu đếm thoe chiều thuận đến ngày sinh, lùi lại một cung,
		# an Ân Quang.
		# Thiên Quý − Xem Văn Khúc ở cung nào, kể cung ấy là mồng một,
		# !!! bắt đầu đếm theo chiều nghịch đến ngày sinh, lùi lại một cung,
		# an Thiên Quý.!!!
		# ??? Thiên Quý ở đối cung của Ân Quang qua trục Sửu Mùi mới chính xác???

		$viTriAnQuang = $helper->dichCung($viTriVanXuong, [$nn - 2]);
		$diaBan->nhapSao($viTriAnQuang, [$this->saoAnQuang]);

		$viTriThienQuy = $helper->dichCung(2, [2 - $viTriAnQuang]);
		$diaBan->nhapSao($viTriThienQuy, [$this->saoThienQuy]);

		$viTriThienKhoi = $helper->timThienKhoi($canNam);
		$diaBan->nhapSao($viTriThienKhoi, [$this->saoThienKhoi]);

		$viTriThienViet = $helper->dichCung(5, [5 - $viTriThienKhoi]);
		$diaBan->nhapSao($viTriThienViet, [$this->saoThienViet]);

		$viTriThienHu = $helper->dichCung(7, [$chiNam - 1]);
		$diaBan->nhapSao($viTriThienHu, [$this->saoThienHu]);

		$viTriThienKhoc = $helper->dichCung(7, [-$chiNam + 1]);
		$diaBan->nhapSao($viTriThienKhoc, [$this->saoThienKhoc]);

		$viTriThienTai = $helper->dichCung($diaBan->cungMenh, [$chiNam - 1]);
		$diaBan->nhapSao($viTriThienTai, [$this->saoThienTai]);

		$viTriThienTho = $helper->dichCung($diaBan->cungThan, [$chiNam - 1]);
		$diaBan->nhapSao($viTriThienTho, [$this->saoThienTho]);

		$viTriHongLoan = $helper->dichCung(4, [-$chiNam + 1]);
		$diaBan->nhapSao($viTriHongLoan, [$this->saoHongLoan]);

		$viTriThienHy = $helper->dichCung($viTriHongLoan, [6]);
		$diaBan->nhapSao($viTriThienHy, [$this->saoThienHy]);

		#  Thiên Quan - Thiên Phúc
		list($viTriThienQuan, $viTriThienPhuc) = $helper->timThienQuanThienPhuc($canNam);
		$diaBan->nhapSao($viTriThienQuan, [$this->saoThienQuan]);
		$diaBan->nhapSao($viTriThienPhuc, [$this->saoThienPhuc]);

		$viTriThienHinh = $helper->dichCung(10, [$tt - 1]);
		$diaBan->nhapSao($viTriThienHinh, [$this->saoThienHinh]);

		$viTriThienRieu = $helper->dichCung($viTriThienHinh, [4]);
		$diaBan->nhapSao($viTriThienRieu, [$this->saoThienRieu, $this->saoThienY]);

		$viTriCoThan = $helper->timCoThan($chiNam);
		$diaBan->nhapSao($viTriCoThan, [$this->saoCoThan]);

		$viTriQuaTu = $helper->dichCung($viTriCoThan, [-4]);
		$diaBan->nhapSao($viTriQuaTu, [$this->saoQuaTu]);

		$viTriVanTinh = $helper->dichCung($viTriKinhDuong, [2]);
		$diaBan->nhapSao($viTriVanTinh, [$this->saoVanTinh]);

		$viTriDuongPhu = $helper->dichCung($viTriVanTinh, [2]);
		$diaBan->nhapSao($viTriDuongPhu, [$this->saoDuongPhu]);

		$viTriQuocAn = $helper->dichCung($viTriDuongPhu, [3]);
		$diaBan->nhapSao($viTriQuocAn, [$this->saoQuocAn]);

		# Thai phụ - Phong Cáo
		$viTriThaiPhu = $helper->dichCung($viTriVanKhuc, [2]);
		$diaBan->nhapSao($viTriThaiPhu, [$this->saoThaiPhu]);

		$viTriPhongCao = $helper->dichCung($viTriVanKhuc, [-2]);
		$diaBan->nhapSao($viTriPhongCao, [$this->saoPhongCao]);

		# Thiên giải - Địa giải
		#    Theo cụ Thiên Lương: Lấy cung Thân làm tháng Giêng, đếm thuận nhưng
		#    nhảy cung là Thiên giải. Một số trang web đếm nhưng không nhảy cung???
		#    Liệu phương cách nào đúng?

		$viTriThienGiai = $helper->dichCung(9, [(2 * $tt) - 2]);
		$diaBan->nhapSao($viTriThienGiai, [$this->saoThienGiai]);

		$viTriDiaGiai = $helper->dichCung($viTriTaPhu, [3]);
		$diaBan->nhapSao($viTriDiaGiai, [$this->saoDiaGiai]);

		# Thiên la - Địa võng, Thiên thương - Thiên sứ
		$viTriThienLa = 5;
		$diaBan->nhapSao($viTriThienLa, [$this->saoThienLa]);

		$viTriDiaVong = 11;
		$diaBan->nhapSao($viTriDiaVong, [$this->saoDiaVong]);

		$viTriThienThuong = $diaBan->cungNoboc;
		$diaBan->nhapSao($viTriThienThuong, [$this->saoThienThuong]);

		$viTriThienSu = $diaBan->cungTatAch;
		$diaBan->nhapSao($viTriThienSu, [$this->saoThienSu]);

		# Vòng Thiên mã
		$viTriThienMa = $helper->timThienMa($chiNam);
		$diaBan->nhapSao($viTriThienMa, [$this->saoThienMa]);

		$viTriHoaCai = $helper->dichCung($viTriThienMa, [2]);
		$diaBan->nhapSao($viTriHoaCai, [$this->saoHoaCai]);

		$viTriKiepSat = $helper->dichCung($viTriThienMa, [3]);
		$diaBan->nhapSao($viTriKiepSat, [$this->saoKiepSat]);

		$viTriDaoHoa = $helper->dichCung($viTriKiepSat, [4]);
		$diaBan->nhapSao($viTriDaoHoa, [$this->saoDaoHoa]);

		# Phá toái
		$viTriPhaToai = $helper->timPhaToai($chiNam);
		$diaBan->nhapSao($viTriPhaToai, [$this->saoPhaToai]);

		# Đẩu quân
		$viTriDauQuan = $helper->dichCung($chiNam, [-$tt + $gioSinh]);
		$diaBan->nhapSao($viTriDauQuan, [$this->saoDauQuan]);

		#  Tứ Hóa
		# An theo 10 câu của cụ Thiên Lương trong cuốn
		# Số tử vi dưới mắt khoa học
		switch ($canNam) {
			case 1:
				$viTriHoaLoc = $viTriLiemTrinh;
				$viTriHoaQuyen = $viTriPhaQuan;
				$viTriHoaKhoa = $viTriVuKhuc;
				$viTriHoaKy = $vitriThaiDuong;
				break;
			case 2:
				$viTriHoaLoc = $viTriThienCo;
				$viTriHoaQuyen = $viTriThienLuong;
				$viTriHoaKhoa = $viTriTuVi;
				$viTriHoaKy = $viTriThaiAm;
				break;
			case 3:
				$viTriHoaLoc = $viTriThienDong;
				$viTriHoaQuyen = $viTriThienCo;
				$viTriHoaKhoa = $viTriVanXuong;
				$viTriHoaKy = $viTriLiemTrinh;
				break;
			case 4:
				$viTriHoaLoc = $viTriThaiAm;
				$viTriHoaQuyen = $viTriThienDong;
				$viTriHoaKhoa = $viTriThienCo;
				$viTriHoaKy = $viTriCuMon;
				break;
			case 5:
				$viTriHoaLoc = $viTriThamLang;
				$viTriHoaQuyen = $viTriThaiAm;
				$viTriHoaKhoa = $viTriHuuBat;
				$viTriHoaKy = $viTriThienCo;
				break;
			case 6:
				$viTriHoaLoc = $viTriVuKhuc;
				$viTriHoaQuyen = $viTriThamLang;
				$viTriHoaKhoa = $viTriThienLuong;
				$viTriHoaKy = $viTriVanKhuc;
				break;
			case 7:
				$viTriHoaLoc = $vitriThaiDuong;
				$viTriHoaQuyen = $viTriVuKhuc;
				$viTriHoaKhoa = $viTriThienDong;
				$viTriHoaKy = $viTriThaiAm;
				break;
			case 8:
				$viTriHoaLoc = $viTriCuMon;
				$viTriHoaQuyen = $vitriThaiDuong;
				$viTriHoaKhoa = $viTriVanKhuc;
				$viTriHoaKy = $viTriVanXuong;
				break;
			case 9:
				$viTriHoaLoc = $viTriThienLuong;
				$viTriHoaQuyen = $viTriTuVi;
				$viTriHoaKhoa = $viTriThienPhu;
				$viTriHoaKy = $viTriVuKhuc;
				break;
			case 10:
				$viTriHoaLoc = $viTriPhaQuan;
				$viTriHoaQuyen = $viTriCuMon;
				$viTriHoaKhoa = $viTriThaiAm;
				$viTriHoaKy = $viTriThamLang;
				break;
			default:
				$viTriHoaLoc = '';
				$viTriHoaQuyen = '';
				$viTriHoaKhoa = '';
				$viTriHoaKy = '';
		}

		$diaBan->nhapSao($viTriHoaLoc, [$this->saoHoaLoc]);
		$diaBan->nhapSao($viTriHoaQuyen, [$this->saoHoaQuyen]);
		$diaBan->nhapSao($viTriHoaKhoa, [$this->saoHoaKhoa]);
		$diaBan->nhapSao($viTriHoaKy, [$this->saoHoaKy]);

		#  An Lưu Hà - Thiên Trù
		# Sách cụ Thiên Lương không đề cập đến 2 sao này
		# Mong mọi người kiểm chứng
		list($viTriLuuHa, $viTriThienTru) = $helper->timLuuTru($canNam);
		$diaBan->nhapSao($viTriLuuHa, [$this->saoLuuHa]);
		$diaBan->nhapSao($viTriThienTru, [$this->saoThienTru]);

		# An Tuần, Triệt
		$ketThucTuan = $helper->dichCung($chiNam, [10 - $canNam]);
		$viTriTuan1 = $helper->dichCung($ketThucTuan, [1]);
		$viTriTuan2 = $helper->dichCung($viTriTuan1, [1]);
		$diaBan->nhapTuan([$viTriTuan1, $viTriTuan2]);

		list($viTriTriet1, $viTriTriet2) = $helper->timTriet($canNam);
		$diaBan->nhapTriet([$viTriTriet1, $viTriTriet2]);

		return $diaBan;
	}

	public function luanGiaiChinhTinh () {
		$saoTuVi = [
			'global' => '<p><strong>SAO TỬ VI</strong><br />Là Vua trong các sao thuộc Tử Vi, chủ về quyền lực, người làm chủ, năng lực lãnh đạo, khí chất tôn quý, bậc vương giả.<br />Tử vi tọa cung Mệnh, tín chủ toát lên khí chất tôn quý, tướng mạo thần thái quý phái, rạng ngời, điềm đạm, ánh mắt trong sáng có hồn. Đôi khi trong suy nghĩ cũng như trong việc làm cảm thấy sự đơn độc, lạc lõng. Cuộc sống có thể không quá vinh hoa, phú quý nhưng được hưởng phúc và nhẹ nhàng. Tai ách hay sóng gió tới cũng dễ dàng vượt qua và đạt được chí nguyện cuộc đời.<br />Tính cách liêm chính ngay thẳng, sống có trách nhiệm, một vẻ cá tính, tự tin, có lòng tự trọng cao, một dạ lương thiện, có lòng nhân hậu, biết nghĩ cho người khác, đặc biệt rất sòng phẳng, phân minh rõ ràng. Là mẫu người mục tiêu rõ ràng, đa mưu túc trí, giỏi bảo vệ ý kiến bản thân, thường dành lợi thế trong những cuộc tranh luận. Là mẫu người Thông minh, học rộng, nhiều trải nghiệm phong phú, có năng lực đặc biệt ẩn sâu bên trong, có khả năng lãnh đạo, quản lý nhất quán, nội lực bên trong mạnh. Tinh thần làm việc hết mình, đam mê công việc, đôi khi có sự cầu toàn.<br />Người có sao Tử vi trong cung mệnh, có khả năng thành danh và thành công cao, là mẫu người thích được người khác tôn trọng, thừa nhận, tán thưởng, lắng nghe, thích đưa ra lời khuyên cho người khác, đôi khi cũng dễ tạo ra bất đồng, hiềm khích với người khác. Cái “tôi” khá cao, làm việc nhiệt tình, nghiêm túc, sức chiên đấu bền bỉ, khả năng thay đổi, thích ứng tốt. <br />Nữ giới có sao Tử vi tại cung Mệnh, cốt cách thanh tâm, gia giáo, có dung mạo xinh đẹp, làn da mịn màng, khí thái đoan trang, là mẫu người “ích tử vượng phu”, giỏi việc xã hội, đảm việc nhà, khéo chiều chồng, giỏi chăm con cái. Có số may mắn lấy được những người chồng giỏi, có địa vị trong xã hội, sống được hưởng phúc, tuổi thọ kéo dài và con cái thành đạt . Tại cung mệnh , chính chiếu và bàng chiếu hội được nhiều cát tinh, số mệnh sẽ càng đẹp đẽ và hanh thông hơn. <br />Người có sao Tử vi tọa thủ cung mệnh, thích được người khác tiếp thu ý kiến của bản thân, thích ra lệnh, thường đưa ra kiến nghị cho người khác, nên dễ bị hiềm khích. Do sao Tử vi có khuynh hướng duy trì địa vị tôn quý của mình, hy vọng được người khác tôn trọng và thừa nhận. Vì vậy tính tình thường kiên định, đòi hỏi cái tôi khá cao, làm bất cứ việc gì đều rất nghiêm túc, có khả năng cải tiến đi lên, đồng thời lại mang tính cầu toàn, thích thể hiện mặt tốt đẹp của bản thân ra ngoài; nhiều khả năng do nỗ lực sau này trong công việc thể hiện nổi trội mà trở nên có quyền uy ở một phương diện nào đó. Sau khi thành danh, tất tiếp tục khẳng định cái tôi, nếu chuyên về tri thức hoặc kỹ thuật đặc thù, lấy hiện trạng đột phá, đồng thời biết tiếp thu ý kiến của bạn bè, duy trì bản năng có lòng hiếu kỳ với bất kỳ sự việc nào, tất có vận may được hiển đạt lớn, nếu không sẽ dễ đi theo khuynh hướng hư danh.<br />Sao Tử vi Miếu địa, Vượng Địa hay Đắc địa, được đồng cung với nhiều cát tinh phò tá như Tả phụ, Hữu bật, Thiên khôi, Thiên việt, Văn xương, Văn khúc, cũng hợp như hội hợp được với các sao như Thiên phủ, Thiên tướng, Thái dương, Thái âm, Hoá quyền, Hóa Lộc, Hóa khoa, Lộc tồn, Thiên mã, theo các bộ hoặc trong cùng một cung, thì là cách cục thuận lợi nhất và khả năng có thành công lớn, dễ có phú quý song toàn.<br />Nếu như Tử vi ở bình hòa hay hãm địa, lại thiếu nhiều cát tinh hội hợp, tín chủ khó mà đạt được chí nguyện bản thân, bản có tính tình thất thường hay cô độc, tư tưởng hướng đạo, có duyên với Phật Thánh, theo đường tu đạo.</p>',
			'cung' => [
				1 => '<p><strong>SAO TỬ VI</strong><br />Là Vua trong các sao thuộc Tử Vi, chủ về quyền lực, người làm chủ, năng lực lãnh đạo, khí chất tôn quý, bậc vương giả.<br />Tử vi tọa cung Mệnh, tín chủ toát lên khí chất tôn quý, tướng mạo thần thái quý phái, rạng ngời, điềm đạm, ánh mắt trong sáng có hồn. Đôi khi trong suy nghĩ cũng như trong việc làm cảm thấy sự đơn độc, lạc lõng. Cuộc sống có thể không quá vinh hoa, phú quý nhưng được hưởng phúc và nhẹ nhàng. Tai ách hay sóng gió tới cũng dễ dàng vượt qua và đạt được chí nguyện cuộc đời.<br />Tính cách liêm chính ngay thẳng, sống có trách nhiệm, một vẻ cá tính, tự tin, có lòng tự trọng cao, một dạ lương thiện, có lòng nhân hậu, biết nghĩ cho người khác, đặc biệt rất sòng phẳng, phân minh rõ ràng. Là mẫu người mục tiêu rõ ràng, đa mưu túc trí, giỏi bảo vệ ý kiến bản thân, thường dành lợi thế trong những cuộc tranh luận. Là mẫu người Thông minh, học rộng, nhiều trải nghiệm phong phú, có năng lực đặc biệt ẩn sâu bên trong, có khả năng lãnh đạo, quản lý nhất quán, nội lực bên trong mạnh. Tinh thần làm việc hết mình, đam mê công việc, đôi khi có sự cầu toàn.<br />Người có sao Tử vi trong cung mệnh, có khả năng thành danh và thành công cao, là mẫu người thích được người khác tôn trọng, thừa nhận, tán thưởng, lắng nghe, thích đưa ra lời khuyên cho người khác, đôi khi cũng dễ tạo ra bất đồng, hiềm khích với người khác. Cái “tôi” khá cao, làm việc nhiệt tình, nghiêm túc, sức chiên đấu bền bỉ, khả năng thay đổi, thích ứng tốt. <br />Nữ giới có sao Tử vi tại cung Mệnh, cốt cách thanh tâm, gia giáo, có dung mạo xinh đẹp, làn da mịn màng, khí thái đoan trang, là mẫu người “ích tử vượng phu”, giỏi việc xã hội, đảm việc nhà, khéo chiều chồng, giỏi chăm con cái. Có số may mắn lấy được những người chồng giỏi, có địa vị trong xã hội, sống được hưởng phúc, tuổi thọ kéo dài và con cái thành đạt . Tại cung mệnh , chính chiếu và bàng chiếu hội được nhiều cát tinh, số mệnh sẽ càng đẹp đẽ và hanh thông hơn. <br />Người có sao Tử vi tọa thủ cung mệnh, thích được người khác tiếp thu ý kiến của bản thân, thích ra lệnh, thường đưa ra kiến nghị cho người khác, nên dễ bị hiềm khích. Do sao Tử vi có khuynh hướng duy trì địa vị tôn quý của mình, hy vọng được người khác tôn trọng và thừa nhận. Vì vậy tính tình thường kiên định, đòi hỏi cái tôi khá cao, làm bất cứ việc gì đều rất nghiêm túc, có khả năng cải tiến đi lên, đồng thời lại mang tính cầu toàn, thích thể hiện mặt tốt đẹp của bản thân ra ngoài; nhiều khả năng do nỗ lực sau này trong công việc thể hiện nổi trội mà trở nên có quyền uy ở một phương diện nào đó. Sau khi thành danh, tất tiếp tục khẳng định cái tôi, nếu chuyên về tri thức hoặc kỹ thuật đặc thù, lấy hiện trạng đột phá, đồng thời biết tiếp thu ý kiến của bạn bè, duy trì bản năng có lòng hiếu kỳ với bất kỳ sự việc nào, tất có vận may được hiển đạt lớn, nếu không sẽ dễ đi theo khuynh hướng hư danh.<br />Sao Tử vi Miếu địa, Vượng Địa hay Đắc địa, được đồng cung với nhiều cát tinh phò tá như Tả phụ, Hữu bật, Thiên khôi, Thiên việt, Văn xương, Văn khúc, cũng hợp như hội hợp được với các sao như Thiên phủ, Thiên tướng, Thái dương, Thái âm, Hoá quyền, Hóa Lộc, Hóa khoa, Lộc tồn, Thiên mã, theo các bộ hoặc trong cùng một cung, thì là cách cục thuận lợi nhất và khả năng có thành công lớn, dễ có phú quý song toàn.<br />Nếu như Tử vi ở bình hòa hay hãm địa, lại thiếu nhiều cát tinh hội hợp, tín chủ khó mà đạt được chí nguyện bản thân, bản có tính tình thất thường hay cô độc, tư tưởng hướng đạo, có duyên với Phật Thánh, theo đường tu đạo.</p>',

				2 => '<p><strong>Sao Tử Vi ở Cung Phụ Mẫu</strong></p><p>Trường hợp Tử Vi tọa tại cung Ngọ, Tý có Thiên Phủ hoặc Thiên Tướng, Thất Sát đồng cung, khả năng cao gia chủ sẽ được sống trong gia đình có Cha mẹ giàu sang, sống thọ, được thừa hưởng gia sản.</p><p>Trường hợp Tử vi hội hợp cùng Thất Sát, khả năng có cha mẹ bất hòa, hoặc gia chủ bất hòa với cha mẹ mình.</p><p>Tử Vi hội hợp Phá Quân hay Tham Lang đồng cung, kết hợp nhiều hung tinh có thể dẫn tới việc đương số hình khắc cha mẹ, sống kiếp mồ côi hoặc phải xa cách cha mẹ, phúc mỏng, buồn tủi.</p>',

				3 => '<p><strong>Sao Tử Vi ở cung Phúc Đức</strong><br />Sao Tử Vi ở cung Phúc Đức tốt nhất là được đóng tại cung Ngọ, Tý<br />Tử vi hội hộp cùng Thiên Phủ hay Thiên Tướng được hưởng phúc lâu dài, ít tai họa, dòng họ danh giá, phú quý.<br />Hội hợp cùng Thất Sát: bản thân phải lưu lạc, lập nghiệp xa quê mới tốt.<br />Tử Vi Phá Quân đồng cung, Tử Vi Tham Lang đồng cung hay Tử Vi gặp sát tinh, nhất là Địa Không, Địa Kiếp, Kình Dương , Đà La, Linh tinh, Hỏa tinh, đây đượ coi là những trường hợp này bất lợi nhất, dẫn tới tình cảm gia đình nhạt nhòa, xa nhà, họ hàng xa lánh, sống trong khó khăn, vất vả, nếu có được khá giả thì thân thể yếu mệt.</p>',

				4 => '<p><strong>Sao Tử Vi ở cung Điền trạch</strong></p><p>Được đặt tại các vị trí tốt, nhất là được hội hợp cùng cát tinh thì  gia chủ được thịnh vượng về nhà cửa, đất đai, của cải giàu có phát triển, nhiều khi gia chủ còn được thừa hưởng gia sản hoặc tự tay làm nên sẽ càng được thịnh vượng.</p><p>Sao Tử Vi tọa tại cung Điền trạch hội hợp cùng với sao Thất Sát, Phá Quân, Tham Lang thì khó giữ được của, nếu không may gặp thêm hung tinh, sát tinh thì có thể hay phải phá sản, sa sút rồi khánh kiệt.</p>',

				5 => '<p><strong>Sao Tử Vi ở Cung Quan Lộc</strong></p><p>Tử Vi hội hợp cùng với Thiên Phủ tại cung Quan Lộc, được làm việc tại các cơ quan công quyền, công ty lớn, giữ vị trí cao, có quyền uy, có cơ hội thăng tiến.</p><p>Tử vi hội hợp cùng Thiên Tướng, gia chủ có tính cách độc tài, mưu quyền đoạt vị, quyết đoán hơn người.</p><p>Hội hợp cùng Thất Sát thì dễ theo đường binh nghiệp, công an cảnh sát.</p><p>Tử vi đồng cung với Phá Quân, Tham Lang gia chủ cần có tâm lý đối mặt với thăng trầm, tai họa, bất mãn trong quan trường có thể chạy sang kinh doanh bên ngoài, nhưng cũng rất thành công, sung túc.</p>',

				6 => '<p><strong>Sao Tử Vi ở cung Nô Bộc</strong></p><p><strong>Sao Tử Vi ở Cung Huynh Đệ</strong></p><p>Có Tử Vi nói chung: có anh cả, chị cả</p><p>Tử Vi ở Ngọ hoặc đồng cung với Thiên Phủ, Thất Sát: anh em đông, khá giả, hòa thuận</p><p>Với Thiên Tướng: khá giả nhưng hay tranh cãi bất hòa.</p><p>Đồng cung với Phá Quân: có anh em dị bào, xa cách, bất hòa</p><p>Đồng cung với Tham Lang: ly tán, nghèo khổ, vất vả</p><p>Gặp sát tinh: anh em tử trận, hao hụt, lần hồi, không có anh em hay anh chị em là côn đồ, du đãng, đĩ điếm.</p>',

				7 => '<p><strong>Sao Tử Vi Cung Thiên Di</strong></p><p>Trường hợp Tử Vi ở Ngọ, Tý, với cách cục Tử Phủ Vũ Tướng hay Tử Sát đều là những cách cục tốt: gia chủ ra xã hội hay gặp thuận lợi, hanh thông, giao thiệp rộng rãi với người có địa vị lại hay gặp may mắn, được tín nhiệm nể trọng.</p><p>Tử vi hội hợp cùng Phá Quân hay Tham Lang, có nhiều sát tinh được coi là cách cục xấu, gia chủ thường lâm vào nghịch cảnh, tai tiếng, thị phi, chèn ép, tha phương cầu thực, bị oan ức, vất vả hay cuối cùng không đạt được chí nguyện cuộc đời.</p>',

				8 => '<p><strong>Sao Tử Vi ở Cung Tật Ách</strong></p><p>Tử Vi ở Tật Ách là điềm may mắn, phần lớn sẽ giúp gia chủ tránh được nhiều tai ương, bệnh hiểm nghèo hay tai nạn. Tử vi ở Tật ách như một sao có tác dụng hóa giải cực mạnh, tương tự như Thiên Phủ, Thiên Lương vậy. Và ngay cả khi hội cùng hung tinh như Thất Sát tjif Tử vi tại Tật ách cũng có giá trị hóa giải mạnh mẽ.</p>',

				9 => '<p><strong>Sao Tử Vi ở Cung Tài Bạch</strong></p><p>Tử Vi ở vị trí miếu, vượng hay đắc địa không gặp Tuần Triệt hay sát tinh, cuộc sống trở nên sung túc và bình an.</p><p>Hội hợp cùng Thiên Phủ, Vũ Khúc, chủ về tiền bạc, tài chính, thì càng thịnh vượng, sáng rõ hơn nữa.</p><p>Hội hợp cùng Thất Sát: làm giàu một cách mạnh mẽ, dễ dàng, nhanh chóng.</p><p>Nếu Sao Tử Vi ở Cung Tài Bạch tọa tại Tý thì kém giàu hơn các trường hợp nói trên.</p><p>Đồng cung với Phá Quân tuy có phá sản, nghèo khổ nhưng cuộc sống dần trở nên sung túc, dư dả.</p><p>Đồng cung với Tham thì rất bình thương, tuy có của cải kế thừa nhưng của cải có xu hướng giảm sút, tiêu tán đi.</p><p>Sao Tử Vi ở Cung Tài Bạch gặp nhiều sát tinh nặng sẽ có nguy cơ phá sản nhiều lần.</p>',

				10 => '<p><strong>Sao Tử Vi Cung Tử Tức</strong><br />ở Miếu Địa, Vượng Địa hay Đắc địa hay đồng cung với Thiên Phủ, Thiên Tướng thì được có cả con trai lẫn con gái, đông con, con cái khá giả, số được nhờ con.<br />Tử vi hội cùng Thất Sát thì khó nuôi con, nhất là thêm các sao xấu và hiếm muộn thì ít con, muộn con.<br />Hội hợp cùng với Phá Quân, Tham Lang thì ít con, con cái có xu hướng sống riêng, xung khắc với cha mẹ.</p>',

				11 => '<p><strong>Sao Tử Vi ở Cung Phu Thê</strong></p><p>Tại Ngọ, Sao Tử Vi hội hợp với Thiên Phủ, gia đình thịnh vượng, vui vẻ, hòa hợp, được sống trong khá giả đến tận hưởng giàu sang, chung thủy, hòa hợp đến lúc bạc đầu.</p><p>Tử Vi hội hợp cùng Thiên Tướng, người vợ và người chồng đều là những người có nhiều trải nghiệm va vấp, cứng cỏi, có bản lĩnh, cá tính, tựu chung là có tầm. Cuộc sống gia đình thường hòa hợp khi về chung một nhà, về sau lại đôi khi có những tranh cãi, xích mích không đáng kế, về cơ bản cuộc sống đủ đầy, giàu có. Vợ hoặc chồng thường là con trưởng, trai nên lấy vợ là trưởng nữ, gái nên lấy chồng là trưởng nam, chồng nên nhiều tuổi hơn vợ.</p><p>Tử vi gặp Phá Quân, có sự xung đột, bất đồng, rất dễ dẫn tới chia ly, có sự xung đột hay chia ly, sống chung với nhau hay hờn giận, nên lấy vợ nhiều tuổi hơn.</p><p>Tử vi gặp Tham Lang, vợ chồng hay xung đột, cãi vã, bất hòa, nên lập gia đình muộn mới mong được an hưởng hạnh phúc và bình an, chồng hoặc vợ rất hay ghen tuông.</p><p>Tử vi hội hợp cùng sao Thất Sát, gia chủ nên lập gia đình muộn, cùng sự tiết chế nhường nhịn trong quan hệ vợ chồng mới mong êm ấm, hạnh phúc và được hưởng phú qúy trọn đời</p>',

				12 => '<p><strong>Sao Tử Vi ở Cung Huynh Đệ</strong></p><p>Gia đình có anh cả, chị cả hoặc là anh cả trong nhà, trong nhà có tiếng nói với anh em, ra xã hội có nhiều anh em thân tình, coi nhau như ruột thịt.</p><p>Tử Vi ở Ngọ hoặc đồng cung với Thiên Phủ, Thất Sát:  anh em đông, khá giả, hòa thuận.</p><p>Tử vi hội hợp Thiên Tướng: anh em khá giả, có điều kiện nhưng hay tranh cãi bất hòa.</p><p>Tử vi đồng cung với Phá Quân: ít anh em tốt, vào sinh ra tử còn không thể bền lâu, anh em trong nhà xa cách, bất hòa, bằng mặt không bằng lòng.</p><p>Tử vi hội hợp với Tham Lang: gia cảnh khó khăn, vào cảnh ly tán, nghèo khổ, vất vả.</p><p>Tử vi gặp nhiều sát tinh: dễ mất anh em, hao hụt, đơn độc không có anh chị em hay anh chị em có cũng như không, sống lang bạt, tranh cãi và nhạt nhẽo.</p>',
